mapper.select:
SELECT * FROM member
WHERE memberId = #{memberId}
AND memberPw = #{memberPw}

SELECT * FROM member
WHERE memberName = #{memberName}
AND memberPhone = #{memberPhone}

SELECT * FROM member
WHERE memberId = #{memberId} 
AND memberName = #{memberName}
AND memberPhone = #{memberPhone}

mapper.update:
UPDATE member
SET memberPw = #{memberPw}
WHERE memberEmail = #{memberEmail}
